import React from "react";

export default function About() {
  return (
    <div className="min-h-screen bg-white flex flex-col items-center justify-center">
      <h2 className="text-3xl font-semibold text-blue-600">About CourseHub</h2>
      <p className="mt-4 max-w-xl text-center text-gray-600">
        CourseHub is a learning platform that helps students and professionals
        discover courses, track progress, and improve their skills through
        structured content.
      </p>
    </div>
  );
}
